INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (205, 'Thuốc bổ xương khớp cho chó VEGEBRAND Hip & Join Health Dog', 375000, 'https://www.petmart.vn/wp-content/uploads/2016/09/thuoc-bo-xuong-khop-cho-cho-vegebrand-hip-join-health-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Thuốc bổ xương khớp cho chóVEGEBRANDHip & Join Health Dog là sản phẩm dinh dưỡng dành cho tất cả giống chó ở mọi giai đoạn phát triển.', 'Thuốc bổ xương khớp cho chó VEGEBRAND Hip & Join Health Dog với công thức tiên tiến có tính MSM và chứa thành phần Axit Hyaluronic, Axit Hyaluronic giúp đệm khớp hoạt động tốt hơn. Tăng cường khả năng vận động ở chó, giúp chó cưng khỏe mạnh năng động và hoạt bát hơn. Là sản phẩm rất cần thiết cho người bạn bốn chân của bạn. Với hình dạng ưa nhìn có thể cho thú cưng nhai hay trộn với thức ăn của chúng.', 'Mỗi 100g trong thuốc Vegebrand Hip & Join Health Dog có chứa các thành phần dinh dưỡng: Chondroitin, Glucosamine, Polysaccharide Protein, Axit hyaluronic, Axit amin kết hợp, Vitamin, khoáng chất, dầu cá biển sâu, chiết xuất từ hoa cúc, Protein gầy 30%, Chất béo thô 10%, Sợi thô (tối đa) 4%, Độ ẩm (tối đa) 25%, Tro (tối đa) 4%.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (213, 'Dầu tắm chó mèo lông dài TRIXIE Langhaar Shampoo', 180000, 'https://www.petmart.vn/wp-content/uploads/2016/04/dau-tam-cho-meo-long-dai-trixie-langhaar-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Dầu tắm chó mèo lông dàiTRIXIELanghaar Shampoo dành cho tất cả các giống chó. Đặc biệt là những giống chó có bộ lông dài và dày như Poodle, Phốc sóc, Alaska, Samoyed…', 'Dầu tắm chó mèo lông dài TRIXIE Langhaar Shampoo với tác dụng chính trong việc làm mềm mượt lông cho thú cưng. Sữa tắm có nồng độ hóa chất thấp phù hợp với các chú chó có da nhạy cảm dễ bị kích ứng với xà phòng. Sử dụng được cho cả chó con.', 'Thành phầndầu tắm cho chóthân thiện với mọi loại da. Sản phẩm dầu tắm cho chó được chế tác với sự tài tình của các chuyên gia. Giảm bớt tối đa nồng độ hóa chất. Thành phần tác dụng dưỡng ẩm cao làm bóng và mềm mượt lông. Có nguồn gốc chủ yếu từ thiên nhiên, an toàn tuyệt đối cho làn da chó con.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (215, 'Dầu tắm chó mèo chiết xuất tinh dầu TRIXIE Neembaumol Shampoo', 180000, 'https://www.petmart.vn/wp-content/uploads/2016/04/dau-tam-cho-meo-chiet-xuat-tinh-dau-trixie-neembaumol-shampoo-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Dầu tắm chó mèo chiết xuất tinh dầuTRIXIENeembaumol Shampoo chứa một lượng lớn các tinh dầu thiết yếu dành cho thú cưng. Phù hợp với tất cả các giống chó như Poodle, Phốc sóc, Becgie, Alaska, Samoyed…', 'Dầu tắm chó mèo chiết xuất tinh dầu TRIXIE Neembaumol Shampoo có tác dụng lớn trong việc chăm sóc da của thú cưng tốt hơn. Da cún cưng sẽ mềm mại hơn và đặc biệt thân thiện với làn da của thú cưng. Đồng thời tác dụng hiệu quả trong việc ngăn ngừa nhiễm ký sinh trùng gây hại. Bao gồm các loại ve rận, bọ chét… Thành phần dầu tắm gội cho chó bổ sung thêm tinh dầu. Tăng tính năng mềm mượt lông dưỡng ẩm cho da thú cưng. Đồng thời tinh dầu còn giúp se nhỏ lỗ chân lông. Giảm rụng lông tốt cho chó mèo lông dài và lưu giữ hương thơm lâu hơn.', 'Cách sử dụngdầu tắm cho chóTRIXIE Neembaumol Shampoo: Nhẹ nhàng thấm nước dần dần cho đều khắp cơ thể. Đồng thời dùng tay thoa dầu tắm cho chó đã pha loãng lên khắp bề mặt cơ thể chó mèo bắt đầu từ phần cổ xuống thân. Matxa nhẹ nhàng khắp cơ thể từ 1 – 3 phút cho ngấm rồi xả lại bằng nước. Lau, sấy khô lông cho thú cưng.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (217, 'Sữa tắm cho chó diệt khuẩn BBN Bactericidal Shampoo Lemon', 150000, 'https://www.petmart.vn/wp-content/uploads/2015/11/sua-tam-cho-cho-diet-khuan-bbn-bactericidal-shampoo-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm cho chódiệt khuẩnBBNBactericidal Shampoo Lemon với chiết xuất từ quả chanh nguyên chất nhập khẩu từ Bắc Phi có hàm lượng Niacin và hữu cơ tương đối cao giúp diệt khuẩn trên cơ thể thú cưng một cách mạnh mẽ. Phù hợp với tất cả các giống chó như Pug, Poodle, Husky, Alaska, Phốc sóc…', 'Sữa tắm cho chó diệt khuẩn BBN Bactericidal Shampoo Lemon có khả năng làm ức chế sự phát triển của vi khuẩn. Tránh cho chó của bạn mắc phải các bệnh ngoài da. Ví dụ như: ngứa, mẩn đỏ, nhiễm trùng… Giúp cho vật nuôi luôn cảm thấy sạch sẽ và thoải mái.', 'Thời tiết ở Việt Nam dễ khiến cho các chú chó bị tích lũy mồ hôi trong các lỗ chân lông khiến cho chúng phát ra mùi hôi vô cùng khó chịu. Là nguy cơ sinh ra vi khuẩn, ký sinh trùng thường xuyên. Chính vì vậy dầu gội với mùi thơm hoa quả tự nhiên. Có thể khử mùi cơ thể hiệu quả. Tuy giữ mùi thời gian ngắn, nhưng có thể bảo đảm giữ sạch thời gian dài. Nghiên cứu thực tế chứng minh trong suốt một tuần không có mùi lạ nào.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (235, 'Sữa tắm cho chó mèo hương hoa quýt JOYCE & DOLLS Sexy Bloom', 250000, 'https://www.petmart.vn/wp-content/uploads/2013/02/sua-tam-cho-cho-meo-huong-hoa-quyt-joyce-dolls-sexy-bloom-0-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm cho chómèo hương hoa quýtJOYCE DOLLSSexy Bloom với tinh dầu hoa quýt. Dành cho tất cả các giống chó như Poodle, Phốc sóc, Samoyed…', 'Sữa tắm cho chó mèo hương hoa quýt JOYCE & DOLLS Sexy Bloom được để thanh tẩy lông, chống viêm và phát triển các tế bào mới. Sản phẩm này có khả năng cải thiện lưu thông máu và tạo sức đề kháng với các tác động bên ngoài, tạo cho thú cưng cảm giác trầm tĩnh khi sử dụng. Sản phẩm chứa hương thơm hoa tươi, giống như cảm thấy một thông điệp tươi mới cùng hương thơm cây hợp hoan gợi cảm, thêm vào tinh dầu hoa quýt có thể làm sạch lông. Với công thức hương thơm 3 giai đoạn:', 'Chú ý: thời gian tráng nước sạch không nên quá lâu, tránh rửa trôi các thành phần chăm sóc.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (237, 'Sữa tắm chó mèo mùi hương khoai môn BBN Taro Silky', 180000, 'https://www.petmart.vn/wp-content/uploads/2014/09/sua-tam-cho-meo-mui-huong-khoai-mon-bbn-taro-silky-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm chó mèo mùi hương khoai mônBBNTaro Silky với các thành phần từ tinh dầu khoai môn tự nhiên, bơ hạt lanh, chiết xuất collagen. Thành phần hoạt nướng dầu, vitamin E, dầu mầm lúa mì… Sản phẩm đặc biệt dành riêng cho thú cưng bị bạc màu lông, có khả năng tái tạo lại màu lông gốc an toàn, hiệu quả cao.', 'Sản phẩm thích hợp với các giống chó lông có màu nâu đỏ. Mùi hương lâu bền, lưu hương đến 1 tuần sau khi tắm. Với D & C Red No. 33 làm lông sáng bóng hiệu quả. Kết cấu không phải là dung dịch dạng sợi, mà là kết cấu dạng kem, hoặc dạng sữa mềm mịn. Kết cấu mềm mịn như sữa dưỡng ẩm này có hiệu quả đặc biệt cho da và lông của thú cưng. Sau khi tắm thì da của thú cưng sẽ mềm hơn không bị khô ráp, lông còn mềm mượt bung xõa.', 'Sữa tắm chó mèo mùi hương khoai môn BBN Taro Silky kiểm soát chất nhờn. Loại bỏ mùi hôi cơ thể. Khi tắm có hiệu quả mát sa da, loại bỏ bã nhờn tích tụ trên da. Thúc đẩy tế bào hoạt động, kiểm soát chất nhờn. Có thêm các hạt mát sa đặc biệt với những hạt nhỏ tự nhiên, mềm mịn không gây tổn thương da. Đặc biệt là vào mùa hè, để cho thú cưng nhà bạn có một mùa hè sảng khoải, không còn mùi hôi.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (239, 'Sữa tắm trị ve rận bọ chét chó TROPICLEAN Natural Flea Tick Dog Shampoo', 395000, 'https://www.petmart.vn/wp-content/uploads/2023/04/sua-tam-tri-ve-ran-bo-chet-cho-meo-tropiclean-natural-flea-tick-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm trị ve rận bọ chét chóTROPICLEANNatural Flea Tick Dog Shampoo là sản phẩm chăm sóc thú cưng rất hiệu quả để diệt trừ ve, rận, trứng, sâu bọ và côn trùng như muỗi.', 'Sản phẩm không chứa các chất độc hại như pyrethrin hoặc permethrin. Với các thành phần tự nhiên như dầu bạc hà và dầu mè, sản phẩm có tác dụng lâu dài và làm tăng độ bền của hiệu quả diệt côn trùng.', 'TROPICLEAN Natural Flea Tick Dog Shampoo chứa cacao giúp làm dịu da chó bị kích ứng do bị côn trùng đốt. Sản phẩm này an toàn để tắm cho chó trên 12 tuần tuổi và được khuyến khích sử dụng định kỳ.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (251, 'Xịt dưỡng lông cho chó mèo TROPICLEAN Spa Lavish Comfort Cologne Oatmeal & Kiwi', 345000, 'https://www.petmart.vn/wp-content/uploads/2023/04/xit-duong-long-cho-cho-meo-tropiclean-spa-lavish-comfort-cologne-oatmeal-kiwi-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Xịt dưỡng lông cho chó mèoTROPICLEANSpa Lavish Comfort Cologne Oatmeal & Kiwi là một sản phẩm xịt dưỡng lông cho chó mèo với những thông tin hấp dẫn. Được sản xuất tại Hoa Kỳ chỉ sử dụng các thành phần có nguồn gốc tự nhiên và chất lượng cao nhất, sản phẩm này giúp dưỡng lông cho thú cưng của bạn trở nên mềm mại và sáng bóng với mùi hương kiwi mọng nước tươi mát.', 'Sự hòa quyện giữa hương thơm của Oải hương, Bạch trà, Kiwi… cho cảm giác thư giãn sảng khoái. Thành phần chống nắng giúp bảo vệ da bé khỏi tia UV, nuôi dưỡng cho da và lông bóng mượt.', 'Công thức dịu nhẹ của sản phẩm bảo vệ da và lông khỏi tia UV, đồng thời an toàn khi sử dụng cho chó và mèo từ 12 tuần tuổi trở lên. TROPICLEAN Spa Lavish Comfort Cologne Oatmeal & Kiwi còn là một phần của dòng SPA by TropiClean, nơi cung cấp các lựa chọn tắm nhẹ nhàng và sang trọng cùng hương thơm tươi mát cho thú cưng thích được nuông chiều.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (253, 'Sữa tắm cho chó mèo da nhạy cảm TROPICLEAN Spa Lavish Pure Pet Shampoo', 380000, 'https://www.petmart.vn/wp-content/uploads/2023/04/sua-tam-cho-cho-meo-da-nhay-cam-tropiclean-spa-lavish-pure-pet-shampoo-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm cho chómèo da nhạy cảmTROPICLEANSpa Lavish Pure Pet Shampoo là sản phẩm hoàn toàn được sản xuất tại Mỹ, sử dụng chỉ những thành phần có nguồn gốc tự nhiên chất lượng cao. Với những thành phần làm sạch tự nhiên, không xà phòng hay hoá chất, giúp nuôi dưỡng làn da thú cưng khoẻ mạnh và làm cho bộ lông của chúng mềm mại ,bồng bềnh và sạch bóng. Đặc biệt, sản phẩm không gây dị ứng, không làm cay mắt nên vô cùng hữu ích với các bé có làn da nhạy cảm, dễ dị ứng và hoàn hảo cho các boss mặt ”lắm lông”.', 'Đặc biệt, sữa tắm cho chó mèo da nhạy cảm TROPICLEAN Spa Lavish Pure Pet Shampoo rất thích hợp cho những thú cưng có da nhạy cảm hoặc dị ứng, đặc biệt là cho vùng mặt của thú cưng. Ngoài ra, sản phẩm còn mang lại hương hoa Plumeria tươi lâu, giúp thú cưng của bạn thơm tho suốt nhiều giờ đồng hồ. Sản phẩm an toàn khi sử dụng cho chó và mèo từ 12 tuần trở lên và là một cách tuyệt vời để giữ cho thú cưng của bạn luôn sạch sẽ và thơm tho.', 'Hãy nhớ tuân theo hướng dẫn tắm gội trên bao bì của sản phẩm này. Mỗi loại sản phẩm sẽ có cách hoạt động khác nhau và yêu cầu thời gian chờ khác nhau trước khi thú cưng tiếp xúc với nước tắm. Vì vậy, để đảm bảo tối đa hiệu quả, bạn nên đọc kỹ hướng dẫn sử dụng của sản phẩm chống bọ chét và ve trên da và tuân thủ chính xác các chỉ dẫn về thời gian tắm gội cho thú cưng.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (255, 'Sữa tắm cho chó khôi phục màu lông BBN Reddening Shampoo', 150000, 'https://www.petmart.vn/wp-content/uploads/2015/11/sua-tam-cho-cho-khoi-phuc-mau-long-bbn-reddening-shampoo-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm cho chókhôi phục màu lôngBBNReddening Shampoo được thiết kế dành riêng cho các giống chó có lông màu nâu – đỏ. Đặc biệt là chó Poodle. Giúp làm sáng màu lông, tăng sắc tố và tạo độ ẩm phù hợp cho lông của chúng.', 'Sữa tắm cho chó khôi phục màu lông BBN Reddening Shampoo nhập khẩu từ Brazil với chiết xuất quả dâu tây có nguồn gốc từ Anh. Chính vì vậy mà nó có hàm lượng chất Pectin cao hơn so với mức bình thường. Giúp nhanh chóng bổ sung thêm dinh dưỡng cho tế bào lông. Phục hồi hiệu quả sự đàn hồi của lông. Đặc biệt là cho các giống chó lông xù, lông dài, lông xoăn.', 'Hàm lượng chất xơ Pectin có hiệu quả tối đa bảo vệ bề mặt lông của vật nuôi. Làm cho lông không bị ảnh hưởng bởi những tác động xấu từ môi trường bên ngoài. Giúp cho lông luôn giữ được màu gốc và sáng bóng. Với thành phần triết xuất lựu, chất béo Jojoba, vitamin C, A, B2, E và rutin,  axit amin tự do, Axit chlorogenic, dầu Glycine, mỡ lông cừu, sợi thô tế bào dưa chuột, vitamin C, đường cam lộ… Tác dụng quan trọng: chống oxy hóa, bổ sung nước cho lông nhanh chóng. Chiết xuất từ hạt lựu nhập khấu Braxin, chống oxy hóa, chứa nhiều thành phần tự nhiên phenon, Axit tannic và vitamin C, E, lông hấp thụ nhanh nhất. Bổ sung thành phần nước cho lông nhanh nhất. Sau khi tắm lông xõa bông bóng mượt có tính đàn hồi.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (269, 'Sữa tắm chó mèo mùi hương khoai môn BBN Taro Silky', 180000, 'https://www.petmart.vn/wp-content/uploads/2014/09/sua-tam-cho-meo-mui-huong-khoai-mon-bbn-taro-silky-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm chó mèo mùi hương khoai mônBBNTaro Silky với các thành phần từ tinh dầu khoai môn tự nhiên, bơ hạt lanh, chiết xuất collagen. Thành phần hoạt nướng dầu, vitamin E, dầu mầm lúa mì… Sản phẩm đặc biệt dành riêng cho thú cưng bị bạc màu lông, có khả năng tái tạo lại màu lông gốc an toàn, hiệu quả cao.', 'Sản phẩm thích hợp với các giống chó lông có màu nâu đỏ. Mùi hương lâu bền, lưu hương đến 1 tuần sau khi tắm. Với D & C Red No. 33 làm lông sáng bóng hiệu quả. Kết cấu không phải là dung dịch dạng sợi, mà là kết cấu dạng kem, hoặc dạng sữa mềm mịn. Kết cấu mềm mịn như sữa dưỡng ẩm này có hiệu quả đặc biệt cho da và lông của thú cưng. Sau khi tắm thì da của thú cưng sẽ mềm hơn không bị khô ráp, lông còn mềm mượt bung xõa.', 'Sữa tắm chó mèo mùi hương khoai môn BBN Taro Silky kiểm soát chất nhờn. Loại bỏ mùi hôi cơ thể. Khi tắm có hiệu quả mát sa da, loại bỏ bã nhờn tích tụ trên da. Thúc đẩy tế bào hoạt động, kiểm soát chất nhờn. Có thêm các hạt mát sa đặc biệt với những hạt nhỏ tự nhiên, mềm mịn không gây tổn thương da. Đặc biệt là vào mùa hè, để cho thú cưng nhà bạn có một mùa hè sảng khoải, không còn mùi hôi.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (271, 'Sữa tắm trị ghẻ cho chó mèo ALKIN Mitecyn Nursing Shampoo', 230000, 'https://www.petmart.vn/wp-content/uploads/2023/04/sua-tam-tri-ghe-cho-cho-meo-alkin-mitecyn-nursing-shampoo1-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm trị ghẻ cho chó mèoALKINMitecyn Nursing Shampoo chuyên chăm sóc điều trị các bệnh ngoài da và lông cho chó mèo từ 10 tuần tuổi trở lên. Sản phẩm có tác dụng hiệu quả trong việc điều trị ghẻ, giúp chống lại sự phát triển của các loại vi khuẩn và kí sinh trùng trên da thú cưng.', 'Sữa tắm trị ghẻ cho chó mèo ALKIN Mitecyn Nursing Shampoo có khả năng điều chỉnh độ pH trên da của chó mèo và giúp duy trì trạng thái bình thường cho da. Sản phẩm này được sử dụng để chăm sóc định kỳ và giúp điều trị và giảm bớt sự lây lan và làm giảm nhanh chóng tình trạng nổi mụn nhọn, ghẻ ngứa trên da thú cưng', 'Được sản xuất trên dây chuyền công nghệ hiện đại, với công thức chứa các thành phần chiết xuất từ thực vật, hoạt động mạnh mẽ để làm sạch và chăm sóc da và lông của chó mèo. Điều này giúp bảo vệ da và lông khỏi các tác động xấu từ môi trường bên ngoài và tăng cường khả năng tự bảo vệ của chúng.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (273, 'Sữa tắm cho chó diệt khuẩn BBN Bactericidal Shampoo Lemon', 150000, 'https://www.petmart.vn/wp-content/uploads/2015/11/sua-tam-cho-cho-diet-khuan-bbn-bactericidal-shampoo-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm cho chódiệt khuẩnBBNBactericidal Shampoo Lemon với chiết xuất từ quả chanh nguyên chất nhập khẩu từ Bắc Phi có hàm lượng Niacin và hữu cơ tương đối cao giúp diệt khuẩn trên cơ thể thú cưng một cách mạnh mẽ. Phù hợp với tất cả các giống chó như Pug, Poodle, Husky, Alaska, Phốc sóc…', 'Sữa tắm cho chó diệt khuẩn BBN Bactericidal Shampoo Lemon có khả năng làm ức chế sự phát triển của vi khuẩn. Tránh cho chó của bạn mắc phải các bệnh ngoài da. Ví dụ như: ngứa, mẩn đỏ, nhiễm trùng… Giúp cho vật nuôi luôn cảm thấy sạch sẽ và thoải mái.', 'Thời tiết ở Việt Nam dễ khiến cho các chú chó bị tích lũy mồ hôi trong các lỗ chân lông khiến cho chúng phát ra mùi hôi vô cùng khó chịu. Là nguy cơ sinh ra vi khuẩn, ký sinh trùng thường xuyên. Chính vì vậy dầu gội với mùi thơm hoa quả tự nhiên. Có thể khử mùi cơ thể hiệu quả. Tuy giữ mùi thời gian ngắn, nhưng có thể bảo đảm giữ sạch thời gian dài. Nghiên cứu thực tế chứng minh trong suốt một tuần không có mùi lạ nào.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (275, 'Sữa tắm chó mèo mùi hương sữa dừa BBN Sweet Coconut', 180000, 'https://www.petmart.vn/wp-content/uploads/2014/09/sua-tam-cho-meo-mui-huong-sua-dua-bbn-sweet-coconut-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Sữa tắm chó mèo mùi hương sữa dừaBBNSweet Coconut với mùi hương sữa dừa ngọt ngào. Với các thành phần được chiết xuất từ dầu dừa tự nhiên, axit lauric, axit amin, chất xơ protein, vitamin E, chiết xuất collagen, axit trái cây tự nhiên… Sản phẩm sử dụng được cho tất cả các giống chó mèo.', 'Sữa tắm chó mèo mùi hương sữa dừa BBN Sweet Coconut giúp chăm sóc lông mượt mà. Khử mùi hôi triệt để cùng nhiều tính năng hữu hiệu khác dành cho thú cưng của bạn. Sản phẩm bảo vệ lông. Giúp lông thú cưng bóng đẹp. Chuyên dụng cho loài chó lông trắng như Poodle, Phốc sóc, Samoyed… Với thành phần FD & C màu trắng no.1 cùng hương thơm không quá nồng. Mang đến một mùi dừa thơm ngọt. Sau khi tắm, thú cưng sẽ có một mùi thơm quyến rũ, giúp chúng thoải mái trong cả 1 tuần. Kết cấu mềm mịn như sữa dưỡng ẩm này có hiệu quả đặc biệt cho da và lông của thú cưng. Sau khi tắm thì da của thú cưng sẽ mềm hơn không bị khô ráp, lông còn mềm mượt bung xõa.', 'Kiểm soát chất nhờn, loại bỏ mùi hôi cơ thể. Khi tắm có hiệu quả mát sa da, loại bỏ bã nhờn tích tụ trên da, thúc đẩy tế bào hoạt động, kiểm soát chất nhờn. Có thêm các hạt mát sa đặc biệt với những hạt nhỏ tự nhiên, mềm mịn không gây tổn thương da, đặc biệt là vào mùa hè, để cho thú cưng nhà bạn có một mùa hè sảng khoải, không còn mùi hôi. Kết cấu mềm mịn, dưỡng ẩm, giúp lông không bị khô xơ. Dưỡng ẩm hiệu quả với da và lông, để lông thú cưng bóng khỏe mềm mịn. Dạng kem sẽ giúp cho lông thú cưng bung xõa, mềm mượt.', 10);
INSERT INTO sanpham (id, ten, gia, hinhanh, loai, danhmuc, thuonghieu, mota, thanhphan, huongdan, soluong) VALUES (293, 'Thức ăn cho mèo con ROYAL CANIN Kitten', 130000, 'https://www.petmart.vn/wp-content/uploads/2021/06/thuc-an-cho-meo-con-royal-canin-kitten-300x300.jpg', 'phukien', 'Phụ kiện thú cưng', 'Đang cập nhật', 'Thức ăn cho mèoconROYAL CANINKitten dành riêng cho mèo con dưới 12 tháng tuổi.', 'Thức ăn cho mèo con ROYAL CANIN Kitten bao gồm protein từ lòng trắng trứng + Probiotic, chất chống oxy hóa giúp nâng cao sức khỏe, tăng sức đề kháng, bổ sungdinh dưỡng cho mèo, giúp cơ thể mèo phát triển đầy đủ. Men tiêu hóa, Probiotic hỗ trợ tiêu hóa cho mèo, giúp cân bằng lợi khuẩn trong đường ruột. Chất xơ hòa tan FOS: chất làm ngọt tự nhiên, duy trì cân bằng hệ vi sinh đường ruột của mèo.', 'Thức ănhạt cho mèoROYAL CANIN Kitten tạo thói quen ăn uống dựa theo tuổi của mèo, cần cho ăn một ngày 3 lần vào các giờ cố định. Cho ăn tại một chỗ để tạo thói quen tốt cho mèo. Lưu ý không cho ăn quá nhiều. Nên cho mèo ăn thức ăn chế biến riêng, không cho ăn thức ăn thừa của người. Vì thức ăn của người có nhiều thành phần khiến mèo bị rối loạn tiêu hóa, ảnh hưởng đến sức khỏe của mèo. Bảo đảm cung cấp đủ nước uống cho mèo. Nếu thấy nước bị mèo làm bẩn, cần thay nước mới ngay lập tức.', 10);
